import styled from 'styled-components';
import { colors } from 'src/styles';
import BgProject from 'src/assets/bg_project.png';

export const Container = styled.section`
  min-height: 100vh;
  width: 100vw;
  background-color: ${colors.background};
  flex-flow: column nowrap;
`;

export const ContentContainer = styled.div`
  min-height: 85vh;
  background-image: url(${BgProject});
  background-position: left;
  background-size: contain;
  background-repeat: no-repeat;
  flex-flow: column nowrap;
  align-items: center;
  background: green;
`;

export const TitleContainer = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  text-align: center;
  margin-bottom: 2%;
  font-family: 'Noto Sans JP';
  color: ${colors.terciary};

  &:after {
    content: '';
    display: block;
    width: 40px;
    height: 5px;
    background-color: ${colors.secondary};
    margin: 15px auto 0;
  }
`;

export const SubTitle = styled.p`
  font-size: 1rem;
  font-weight: 400;
  text-align: center;
  line-height: 1.5;
  font-family: 'Noto Sans JP';
  width: 50%;
`;

export const ImageContainer = styled.div`
  flex-flow: row wrap;
  width: 60%;
  align-items: center;
  justify-content: center;
  background: red;
`;

export const ThumbnailProject = styled.div`
  display: flex;
  background-color: #ffffff;
  border-color: #f9f9f9;
  border-width: 1px;
  border-radius: 16px;
  overflow: hidden;
  position: relative;
  border: 0 solid #818a91;
`;

export const ThumbnailLink = styled.a`
  margin-left: auto;
  margin-bottom: 20px;
  position: relative;
  display: block;
  width: 100%;
  box-shadow: none;
  text-decoration: none;
`;

export const ThumbnailImage = styled.img`
  height: 100%;
  width: 10%;
  position: absolute;
  top: calc(50% + 1px);
  left: calc(50% + 1px);
  display: block;
`;
