import React from 'react';

import {
  Container,
  ContentContainer,
  TitleContainer,
  ParagraphContainer,
} from './styles';

const About: React.FC = () => {
  return (
    <Container>
      <ContentContainer>
        <TitleContainer>About</TitleContainer>
        <ParagraphContainer>
          My full name is Pedro Hugo da Silva Freire, I am 20 years old, I was
          born and raised in a small town in the interior of Rio Grande do Norte
          called Santo Ant√¥nio, I left there when I was 18 to study in Natal,
          the state capital, where I currently live.
          <br />
          I am a Computer Technician graduated from the Federal Institute of Rio
          Grande do Norte in 2017 and Graduating in Bachelor of Information
          Technology. I also have the React and React-Native course certificate
          from Rocketseat.
          <br />
          <br />I have my own digital agency, Happy File (@happy.file), where I
          work as a graphic designer, designing logos, social media and the arts
          in general. I work with programs such as Prohotoshop, Illustrator,
          Figma and Sony Vegas.
        </ParagraphContainer>
      </ContentContainer>
    </Container>
  );
};

export default About;
