import styled from 'styled-components';
import { colors } from 'src/styles';

export const Container = styled.div`
  min-height: 100vh;
  width: 100vw;
  background-color: ${colors.background};
  flex-flow: column nowrap;
  align-items: center;
`;

export const TitleContainer = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  text-align: center;
  margin-bottom: 2%;

  &:after {
    content: '';
    display: block;
    width: 40px;
    height: 5px;
    background-color: ${colors.secondary};
    margin: 5px auto 0;
  }
`;

export const ParagraphContainer = styled.p`
  font-size: 1.2rem;
  font-weight: 400;
  text-align: justify;
  padding-left: 15rem;
  padding-right: 15rem;
  line-height: 1.5;
`;
